const ethers = require('ethers');

const getTokenIdCounter = async (providerOrWallet, abi, contractAddress) => {
    
    //attaching to a deployed contract and interacting with your contract
    const erc721 = new ethers.Contract(contractAddress, abi, providerOrWallet);

    const tokenId = await erc721.counter()
    return tokenId;
      
  }
  
const getTokenOnChainDetails = async (providerOrWallet, abi, contractAddress, tokenID) => {

    //attaching to a deployed contract and interacting with your contract
    const erc721 = new ethers.Contract(contractAddress, abi, providerOrWallet);
    
    const owner = await erc721.ownerOf(tokenID);
    const uri = await erc721.tokenURI(tokenID);
    
    return {owner, uri};
}

module.exports = {getTokenOnChainDetails, getTokenIdCounter}